mapper.select:
SELECT COUNT(mem_id) 
FROM member 
WHERE mem_id = #{param1} AND mem_pw = #{param2}

mapper.update:
UPDATE member SET
	mem_pw = #{mem_pw}
	,mem_nick = #{mem_nick}
	,mem_stat = #{mem_stat}
	,mem_emailchk = #{mem_emailchk}
	, mem_bcode = #{mem_bcode}
	,mem_grade = #{mem_grade}
	,mem_perm_yn = #{mem_perm_yn}
WHERE mem_id = #{mem_id}
